import React from 'react';
import { Route, Routes } from 'react-router-dom';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import CollectionPage from '../collection/collection.component';

import CollectionsOverviewContainer from '../../components/collections-overview/collections-overview.container';
import WithSpinner from '../../components/with-spinner/with-spinner.component';

import { fetchCollectionsStartAsync } from '../../redux/shop/shop.actions';
import { selectIsCollectionLoaded } from '../../redux/shop/shop.selectors';

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {

  componentDidMount () {
    const { fetchCollectionsStartAsync } = this.props;
    fetchCollectionsStartAsync();
  }
    render () {

      const {  isCollectionsLoaded } = this.props;

        return (
        <div className='shop-page'>
            <Routes>
              <Route path='' element={<CollectionsOverviewWithSpinner isLoading={isColloctionFetching} />} />
              <Route path= ':collectionId' element={<CollectionPageWithSpinner isLoading={!isCollectionsLoaded} />} />
            </Routes>
        </div>
    )
  }
};

const mapStateToProps = createStructuredSelector({
  isColloctionFetching: selectIsCollectionFetching,
  isCollectionsLoaded: selectIsCollectionLoaded
});

const mapDispatchToProps = dispatch => ({
  fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
});

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
